import React from 'react';
import logo from './logo.svg';
import './App.css';
import {BrowserRouter as Router, Route, Link} from 'react-router-dom';
import Home from './components/Home';
import Crypto from './components/Crypto';
import {Navbar, Jumbotron, Container, Col} from 'react-bootstrap';
import AllergenNav from './components/AllergenNav';
import Allergen from './components/Allergen';
import Title from './components/Title';
import {Copy} from './components/Title';
import './genX.scss';


function App() {
  const {Machine, interpret} = XState;

const buttonMachine = Machine({
  id: 'buttonStates',
  initial: 'closed',
  states: {
    closed: { on: { HOVER: 'fanStart' } },

    fanStart: {
      on: {
        TRANSITIONEND: 'fanComplete',
        HOVEROUT: 'closed'
      }
    },

    fanComplete: {
      on: { 
          HOVEROUT: 'closed'
      }
    },
  }
});

const iconMachine = Machine({
  id: 'iconStates',
  initial: 'inactive',
  states: {
    inactive: { on: { HOVER: 'active' } },    
    active: {
      on: {
        HOVEROUT: 'inactive'
      }
    },
  }
});


const buttonService = interpret(buttonMachine)
.onTransition( state => console.log(state.value))
.start();

const iconService = interpret(iconMachine)
.onTransition( state => console.log(state.value))
.start();


const quadButton = document.querySelector('.quad-button');
const letters = document.querySelectorAll('.letter');

quadButton.addEventListener('mouseenter', function(e) {
  const state = buttonService.send('HOVER');
  quadButton.dataset['buttonState'] = state.value;
});

const iconServiceHover = (e) => {
  const state = iconService.send('HOVER');
  e.target.dataset['iconState'] = state.value;
};

const iconServiceHoverOut = (e) => {
  const state = iconService.send('HOVEROUT');
  e.target.dataset['iconState'] = state.value;
};

quadButton.addEventListener('transitionend', function(e) {
  const state = buttonService.send('TRANSITIONEND');
  quadButton.dataset['buttonState'] = state.value;

  if(state.value === 'fanComplete') {
   [...letters].forEach((letter) => {
    letter.addEventListener('mouseenter', iconServiceHover);
  });

  [...letters].forEach((letter) => {
    letter.addEventListener('mouseleave', iconServiceHoverOut);
  });    
  }
});

quadButton.addEventListener('mouseleave', function(e) {
  const state = buttonService.send('HOVEROUT');
  quadButton.dataset['buttonState'] = state.value;


  [...letters].forEach((letter) => {
    console.log('removing the listeners');
  letter.removeEventListener('mouseenter', iconServiceHover);
});

[...letters].forEach((letter) => {
  letter.removeEventListener('mouseleave', iconServiceHoverOut);
});

});




  return (
    <Container>
      <link
          rel="stylesheet"
          href="https://maxcdn.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
          integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
          crossOrigin="anonymous"
      />
      <div className="header-nav">
        <AllergenNav/>
      </div>
      <Col>
        <div className="box">
          <Title text="Allergen Check" />
          <Copy />
        </div>
        <Router> 
        <div className="App">
          <Jumbotron>
            <Route path="/" exact component={Home}/>
            <Route path="/crypto" component={Crypto} />
            <Route path='/allergen' component={Allergen} />
          </Jumbotron>
        </div>
      </Router>
      </Col>
    </Container>
  );
}

export default App;
