import React from 'react';
import {Form, Button} from 'react-bootstrap';
import {useState} from 'react';

const Title = props => (
    <h1 className="title">{props.text}</h1>
  );

export function Copy() {
    const [objects, setActiveObjects] = useState([{value: "No results"}])
    const [seleced, setSelected] = useState("No Results");
    const registerAllergies = (e) => {
        console.log(e)
        setActiveObjects(e.option.active)
        const allergies = objects.map(allergy =>
            <li>{allergy}</li>
        );
        setSelected(allergies);
    }

    return (
        <Form>
            <Form.Group controlId="exampleForm.ControlSelect1">
                <Form.Label>Select your allergies</Form.Label>
                <Form.Control onChange={(e) => {
                    console.log(e.target.option);
                    setSelected(e.target.seleced)} } as="select" multiple>
                    <option >Peanuts</option>
                    <option>Eggs</option>
                    <option>Tree Nuts</option>
                    <option>Dairy</option>
                    <option>Gluten</option>
                </Form.Control>
            </Form.Group>
            <Button onClick={registerAllergies} variant="primary" size="md" active>
                Submit
            </Button>
            <div>
                <ul>{seleced}</ul>
            </div>
        </Form>
    )
}

export default Title;
