import React from 'react';
import {Container, Row, Col, Button, Jumbotron} from 'react-bootstrap';
import {useState} from 'react';

const Allergen = () => {
    const [submitResp, setResp] = useState("No results");

    const fetchTranslation = () => {
        fetch('http://localhost:8080/form-submit')
            .then(respone => console.log(respone.json()))
            .then(response => response.text()
                .then(text => {
                    console.log(text);
                    setResp(text);
                }))
            .catch(error => console.log(error))
    }

    return(
        <Container>
            <Row>
                <Col>This is Column: 1</Col>
                <Button onClick={fetchTranslation}>check these allergies</Button>
            </Row>
            <Row>
                <Jumbotron>{submitResp}</Jumbotron>
            </Row>
        </Container>
    )
}



export default Allergen;
